import './App.css';

class App extends Component{
//function App() {

   constructor(){
       super(); 
       this.state = {
           title: 'To-Do List',
           item:[]
       }
   } 

//make ajax calls here
componentDidMount(){
    console.log('COMPONENT HAS MOUNTED')
}

addItem(event){
    event.preventDefault;
    console.log('in method');
}

render() {
  let title = this.state.title; 
  return (
    <div className="App">
      <h1>{title}</h1>
      <form ref="itemForm">
        <input type="text" ref="item_name" placeholder="Enter task" /> 
        <button onClick = {this.addItem.bind(this)}>Add Item </button>
      </form>
    </div>
  );
    }
}

export default App;
